INSERT INTO `tbl_product` (`pro_id`, `pro_name`, `original_price`, `promotional_price`, `quantity`, `short_desc`, `detail_desc`, `pro_image`, `cat_id`, `brand_id`, `status_pro`) VALUES
(40, 'Camera cảnh báo Lupa Bullet', 2999999, 2530000, 67, 'Camera cảm báo Lupa Bullet thuộc bộ giải pháp an ninh AI Camera Lupa mới nhất của Lumi. Đây là mẫu camera cảnh báo hiện đại với nhiều tính năng nổi bật giúp mang đến một “người bảo vệ” 24/24 cho ngôi nhà của bạn. Đặc biệt với việc tích hợp AI – trí tuệ nhân tạo giúp xử lý hình ảnh nhanh chóng và cảnh báo tức thời tới người dùng.', '<h2><b>1. T&iacute;nh năng camera cảnh b&aacute;o Lupa Bullet</b></h2>\r\n\r\n<h3><b><i>1.1. T&iacute;nh năng live stream trực tuyến theo thời gian thực</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Người d&ugrave;ng c&oacute; thể th&ocirc;ng qua c&aacute;c nền tảng&nbsp; tr&igrave;nh duyệt web, VLC, RXCamView hoặc Lumilife nhờ v&agrave;o khả năng kết nối mạng của camera.</li>\r\n	<li aria-level=\"1\">Những h&igrave;nh ảnh được ghi lại đều theo chuẩn thời gian thực gi&uacute;p người d&ugrave;ng kiểm so&aacute;t an ninh 24/7.</li>\r\n	<li aria-level=\"1\">Lưu &yacute;: Với tr&igrave;nh duyệt web người d&ugrave;ng chỉ c&oacute; thể xem live stream tr&ecirc;n tr&igrave;nh duyệt Internet Explorer. Với ứng dụng LumiLife kh&aacute;ch h&agrave;ng cần th&ecirc;m v&agrave;o AI Camera Hub mới c&oacute; thể xem được Live Stream.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.2. H&igrave;nh ảnh ghi lại r&otilde; r&agrave;ng, sắc n&eacute;t cả trong điều kiện thiếu s&aacute;ng</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera Bullet c&oacute; thiết kế hệ thống đ&egrave;n LED chiếu s&aacute;ng v&agrave;o ban đ&ecirc;m k&egrave;m độ ph&acirc;n giải 2K gi&uacute;p ghi lại h&igrave;nh ảnh c&oacute; m&agrave;u sắc sống động, chi tiết.</li>\r\n	<li aria-level=\"1\">Sản phẩm c&oacute; khả năng tự động bật đ&egrave;n LED khi đ&atilde; x&aacute;c định được m&ocirc;i trường xung quanh kh&ocirc;ng c&oacute; &aacute;nh s&aacute;ng như thời tiết &acirc;m u hoặc v&agrave;o ban đ&ecirc;m.</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-14561\" id=\"attachment_14561\"><img alt=\"Hình ảnh ghi lại rõ ràng, sắc nét cả trong điều kiện thiếu sáng\" decoding=\"async\" height=\"849\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2.jpg 850w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-300x300.jpg 300w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-100x100.jpg 100w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-600x599.jpg 600w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-150x150.jpg 150w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-768x767.jpg 768w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Lupa-Bullet-2-50x50.jpg 50w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-14561\">H&igrave;nh ảnh ghi lại r&otilde; r&agrave;ng, sắc n&eacute;t cả trong điều kiện thiếu s&aacute;ng</figcaption>\r\n</figure>\r\n\r\n<h3><b><i>1.3. Hỗ trợ khả năng lưu trữ th&ocirc;ng qua thẻ nhớ micro SD v&agrave; lưu trữ đ&aacute;m m&acirc;y</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera cảnh b&aacute;o Bullet hỗ trợ lưu trữ h&igrave;nh ảnh, video th&ocirc;ng qua thẻ nhớ theo chế độ to&agrave;n thời gian hoặc khi c&oacute; chuyển động.</li>\r\n	<li aria-level=\"1\">Ngo&agrave;i ra, người d&ugrave;ng c&oacute; thể sử dụng lưu trữ đ&aacute;m m&acirc;y c&aacute;c sự kiện ph&aacute;t hiện người trong v&ugrave;ng tiếp cận của camera khi t&iacute;ch hợp với AI Camera Hub.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.4. Gi&aacute;m s&aacute;t an to&agrave;n tuyệt đối nhờ t&iacute;nh năng c&ograve;i h&uacute; v&agrave; đ&egrave;n chớp cảnh b&aacute;o</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Nhờ t&iacute;ch hợp với bộ xử l&yacute; trung t&acirc;m AI Camera Hub t&iacute;ch hợp AI m&agrave; sản phẩm camera Lupa Bullet c&oacute; thể h&uacute; c&ograve;i v&agrave; chớp đ&egrave;n li&ecirc;n tục khi ph&aacute;t hiện kẻ x&acirc;m nhập.</li>\r\n	<li aria-level=\"1\">Đặc biệt t&iacute;nh năng n&agrave;y c&ograve;n c&oacute; thể c&agrave;i đặt l&ecirc;n lịch, điều khiển theo &yacute; muốn của người d&ugrave;ng nhờ v&agrave;o c&agrave;i điều kiện th&ecirc;m camera v&agrave;o AI Camera Hub.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.5. Dễ d&agrave;ng t&iacute;ch hợp với AI Camera Hub, th&ecirc;m v&agrave;o nh&agrave; th&ocirc;ng minh</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Ngo&agrave;i c&aacute;c t&iacute;nh năng được thiết kế ri&ecirc;ng, sản phẩm camera cảnh b&aacute;o Lupa Bullet c&ograve;n c&oacute; thể t&iacute;ch hợp với bộ xử l&yacute; AI Camera Hub để hỗ trợ th&ecirc;m nhiều c&aacute;c t&iacute;nh năng ưu việt kh&aacute;ch.</li>\r\n	<li aria-level=\"1\">Đặc bi&ecirc;t, AI Camera Hub c&ograve;n cung cấp th&ecirc;m một số t&iacute;nh năng ri&ecirc;ng chỉ &aacute;p dụng với Camera Lupa gi&uacute;p tăng cường trải nghiệm của người d&ugrave;ng.</li>\r\n</ul>\r\n\r\n<h2><b>2. Ưu điểm của sản phẩm camera Lupa Bullet trong g&oacute;i giải ph&aacute;p an ninh Ai Camera Lupa</b></h2>\r\n\r\n<h3><b><i>2.1. Khoanh v&ugrave;ng an ninh, cảnh b&aacute;o tức thời nhờ tr&iacute; tuệ nh&acirc;n tạo AI</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bộ giải ph&aacute;p an ninh AI Camera Lupa được t&iacute;ch hợp tr&iacute; tuệ nh&acirc;n tạo gi&uacute;p ph&aacute;t hiện chuyển động của người ch&iacute;nh x&aacute;c tới 97%.</li>\r\n	<li aria-level=\"1\">Sau khi ph&aacute;t hiện người lạ, bộ giải ph&aacute;p an ninh sẽ ngay tức th&igrave; ph&aacute;t &acirc;m thanh, chớp đ&egrave;n xua đuổi kẻ trộm v&agrave; gửi tin nhắn cảnh b&aacute;o tới điện thoại người d&ugrave;ng.</li>\r\n	<li aria-level=\"1\">Từ đ&oacute; gia chủ ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m nhờ &ldquo;người bảo vệ&rdquo; th&ocirc;ng minh biết tự đuổi trộm v&agrave; ngăn chặn h&agrave;nh vi x&acirc;m nhập tr&aacute;i ph&eacute;p d&ugrave; ở bất kỳ đ&acirc;u.</li>\r\n</ul>\r\n\r\n<h3><b><i>2.2. H&igrave;nh ảnh m&agrave;u sắc n&eacute;t, sống động v&agrave;o ban đ&ecirc;m nhờ độ ph&acirc;n giải 2K</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">C&aacute;c chuy&ecirc;n gia c&ocirc;ng nghệ của Lumi v&agrave; Pavana đ&atilde; nghi&ecirc;n cứu, t&iacute;ch hợp đ&egrave;n LED hồng ngoại v&agrave; LED &aacute;nh s&aacute;ng trắng cho camera Lupa.</li>\r\n	<li aria-level=\"1\">Nhờ đ&oacute; cải thiện &aacute;nh s&aacute;ng của khu vực theo d&otilde;i, tăng độ ch&iacute;nh x&aacute;c nhận diện người chuyển động, gi&uacute;p người d&ugrave;ng xem được h&igrave;nh ảnh c&oacute; m&agrave;u v&agrave;o ban đ&ecirc;m.</li>\r\n	<li aria-level=\"1\">Đặc biệt mắt thần ghi h&igrave;nh của bộ giải ph&aacute;p với ống k&iacute;nh 90.4&deg; @3.6mm rộng, c&ugrave;ng độ ph&acirc;n giải 2K gi&uacute;p cho h&igrave;nh ảnh ghi được sắc n&eacute;t, sống động v&agrave;o cả ban ng&agrave;y lẫn ban đ&ecirc;m</li>\r\n</ul>\r\n\r\n<h3><b><i>2.3. Quản l&yacute; chủ động, an to&agrave;n nhờ khả năng lưu trữ lớn tr&ecirc;n Cloud v&agrave; thẻ nhớ</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bộ giải ph&aacute;p an ninh AI Camera Lupa hỗ trợ lưu trữ theo 2 h&igrave;nh thức: Lưu trữ trong thẻ nhớ v&agrave; Lưu trữ đ&aacute;m m&acirc;y (Cloud).</li>\r\n	<li aria-level=\"1\">Với c&ocirc;ng nghệ P2P, b&ecirc;n cạnh app Lumi Life, giải ph&aacute;p an ninh Ai camera Lumi cho ph&eacute;p xem h&igrave;nh ảnh camera trực tuyến nội mạng v&agrave; ngoại mạng th&ocirc;ng qua nhiều nền tảng như tr&igrave;nh duyệt web, ứng dụng VLC.</li>\r\n	<li aria-level=\"1\">B&ecirc;n cạnh t&iacute;nh năng quản l&yacute; chủ động, bộ giải ph&aacute;p an ninh c&ograve;n gi&uacute;p gia chủ dễ d&agrave;ng c&agrave;i đặt th&ocirc;ng số luồng h&igrave;nh ảnh, cấu h&igrave;nh mạng v&agrave; cảnh b&aacute;o c&ograve;i đ&egrave;n ở khắp mọi nơi th&ocirc;ng qua ứng dụng LumiLife</li>\r\n</ul>\r\n\r\n<h3><b><i>2.4. Đa dạng lựa chọn g&oacute;i giải ph&aacute;p an ninh nhờ bộ sản phẩm Camera trong nh&agrave;, ngo&agrave;i trời</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera AI Lupa đều phải trải qua b&agrave;i kiểm tra khắc nghiệt nhằm đảm bảo chất lượng bền bỉ v&agrave; khả năng chống chịu với m&ocirc;i trường khắc nghiệt để gi&uacute;p xua đuổi kẻ x&acirc;m nhập từ v&ograve;ng ngo&agrave;i ng&ocirc;i nh&agrave;.</li>\r\n	<li aria-level=\"1\">Camera Lupa ngo&agrave;i trời c&oacute; chỉ số chống nước ở mức độ cao IP66. Người d&ugrave;ng c&oacute; đa dạng lựa chọn g&oacute;i giải ph&aacute;p an ninh trong nh&agrave; hoặc ngo&agrave;i trời theo nhu cầu sử dụng.</li>\r\n</ul>\r\n\r\n<h3><b><i>2.5. Dễ d&agrave;ng t&iacute;ch hợp v&agrave;o nh&agrave; th&ocirc;ng minh Lumi, gia tăng trải nghiệm sống tiện nghi</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">L&agrave; một phần quan trọng gi&uacute;p ho&agrave;n thiện hệ thống Nh&agrave; th&ocirc;ng minh to&agrave;n diện của Lumi bộ giải ph&aacute;p Ai Camera Lupa dễ d&agrave;ng kết nối với c&aacute;c giải ph&aacute;p smarthome, smart lighting.</li>\r\n	<li aria-level=\"1\">Từ đ&oacute; gi&uacute;p người d&ugrave;ng thiết lập ngữ cảnh dựa v&agrave;o nhu cầu sinh hoạt của gia đ&igrave;nh, n&acirc;ng cấp mức độ tự động h&oacute;a cho ng&ocirc;i nh&agrave; v&agrave; gia tăng trải nghiệm sống tiện nghi.</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-14579\" id=\"attachment_14579\"><img alt=\"Dễ dàng tích hợp với AI Camera Hub của nhà thông minh Lumi\" decoding=\"async\" height=\"849\" loading=\"lazy\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1.jpg 850w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-300x300.jpg 300w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-100x100.jpg 100w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-600x599.jpg 600w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-150x150.jpg 150w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-768x767.jpg 768w, https://lumi.vn/wp-content/uploads/2023/04/lumi-Camera-Turret-ket-noi-voi-AI-camera-hub-1-50x50.jpg 50w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-14579\">Dễ d&agrave;ng t&iacute;ch hợp với AI Camera Hub của nh&agrave; th&ocirc;ng minh Lumi</figcaption>\r\n</figure>\r\n\r\n<h2><b>3. Ứng dụng của camera cảnh b&aacute;o Bullet</b></h2>\r\n\r\n<p>Với những ưu điểm nổi bật v&agrave; t&iacute;nh năng vượt trội, camera Bullet c&oacute; thể ứng dụng cho rất nhiều khu vực kh&aacute;c nhau để bảo vệ v&agrave; gi&aacute;m s&aacute;t an ninh. Cụ thể:</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Biệt thự, nh&agrave; vườn</li>\r\n	<li aria-level=\"1\">C&aacute;c căn hộ chung cư</li>\r\n	<li aria-level=\"1\">C&aacute;c văn ph&ograve;ng c&ocirc;ng sở</li>\r\n	<li aria-level=\"1\">C&aacute;c t&ograve;a nh&agrave; lớn, trung t&acirc;m thương mại</li>\r\n	<li aria-level=\"1\">Bệnh viện, ph&ograve;ng nghi&ecirc;n cứu</li>\r\n	<li aria-level=\"1\">Trường học</li>\r\n	<li aria-level=\"1\">&hellip;</li>\r\n</ul>\r\n', 'upload/images/1.jpg', 1, 2, 0),
(44, 'Camera cảnh báo Lupa Turret', 2699999, 2530000, 82, 'Camera cảnh báo Lupa Turret là dòng sản phẩm mới của Lumi kết hợp cùng Pavana, được các chuyên viên kỹ thuật nghiên cứu trong thời gian dài; nhằm mang đến cho người dùng một giải pháp an ninh tốt nhất hiện nay. Sản phẩm có tính năng giám sát, khoanh vùng an ninh và cảnh báo tức thời khi phát hiện xâm nhập trái phép khi kết hợp cùng trí tuệ nhân tạo AI trong Ai Camera Hub của Lumi. Camera cảnh báo Lupa Turret được coi là mảnh ghép quan trọng trong giải pháp an ninh Ai Camera Lupa mới ra mắt lần này . ', '<h2><b>1. T&iacute;nh năng camera cảnh b&aacute;o Lupa Turret</b></h2>\r\n\r\n<h3><b><i>1.1. Camera live stream trực tuyến theo thời gian thực</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">L&agrave; mẫu&nbsp;<a href=\"https://lumi.vn/san-pham/camera-thong-minh\" rel=\"noopener\" target=\"_blank\"><em><strong>camera th&ocirc;ng minh</strong></em></a>&nbsp;c&oacute; thể kết nối với hệ thống mạng gi&uacute;p người d&ugrave;ng c&oacute; thể xem h&igrave;nh ảnh camera trực tuyến nội mạng v&agrave; ngoại mạng.</li>\r\n	<li aria-level=\"1\">Để xem h&igrave;nh ảnh người c&ugrave;ng c&oacute; thể th&ocirc;ng qua những nền tảng tr&igrave;nh duyệt web, VLC, RXCamView hoặc app Lumi Life.</li>\r\n	<li aria-level=\"1\">Lưu &yacute;: Với tr&igrave;nh duyệt web người d&ugrave;ng chỉ c&oacute; thể xem live stream tr&ecirc;n tr&igrave;nh duyệt Internet Explorer. Với ứng dụng LumiLife kh&aacute;ch h&agrave;ng cần th&ecirc;m v&agrave;o AI Camera Hub mới c&oacute; thể xem được Live Stream.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.2. Khả năng lưu trữ dung lượng lớn tr&ecirc;n cloud hoặc th&ocirc;ng qua thẻ nhớ micro SD&nbsp;</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera cảnh b&aacute;o Lupa Turret hỗ trợ lưu trữ h&igrave;nh ảnh, video th&ocirc;ng qua thẻ nhớ theo chế độ to&agrave;n thời gian hoặc khi c&oacute; chuyển động.</li>\r\n	<li aria-level=\"1\">Ngo&agrave;i ra khi th&ecirc;m sản phẩm v&agrave;o AI Camera Hub người d&ugrave;ng c&oacute; thể sử dụng lưu trữ đ&aacute;m m&acirc;y c&aacute;c sự kiện ph&aacute;t hiện người trong v&ugrave;ng tiếp cận của camera.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.3. M&agrave;u sắc sinh động, sắc n&eacute;t ngay cả trong điều kiện ban đ&ecirc;m</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera Turret c&oacute; thiết kế hệ thống đ&egrave;n LED chiếu s&aacute;ng v&agrave;o ban đ&ecirc;m,&nbsp;c&oacute; ống k&iacute;nh 90.4&deg; @3.6mm rộng, c&ugrave;ng độ ph&acirc;n giải 2K&nbsp;gi&uacute;p xem được h&igrave;nh ảnh c&oacute; m&agrave;u sắc n&eacute;t.</li>\r\n	<li aria-level=\"1\">Khi x&aacute;c nhận được kh&ocirc;ng gian xung quanh đ&atilde; tối, sản phẩm n&agrave;y sẽ tự động bật đ&egrave;n LED để cải thiện &aacute;nh s&aacute;ng v&agrave; tăng độ nhận diện ch&iacute;nh x&aacute;c cho khu vực cần gi&aacute;m s&aacute;t.</li>\r\n</ul>\r\n\r\n<h3><b><i>1.4. Cảnh bảo c&ograve;i v&agrave; đ&egrave;n hỗ trợ an ninh an to&agrave;n tuyệt đối</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Với t&iacute;nh năng c&ograve;i h&uacute; v&agrave; đ&egrave;n chớp cảnh b&aacute;o khi c&oacute; đột nhập sẽ gi&uacute;p người d&ugrave;ng dễ d&agrave;ng nhận biết c&oacute; kẻ đột nhập đồng thời gi&uacute;p dọa kẻ đột nhập bỏ chạy.</li>\r\n	<li aria-level=\"1\">C&ograve;i v&agrave; đ&egrave;n chớp c&oacute; thể điều khiển theo &yacute; muốn hoặc bật tắt tự động theo rule khi kết hợp Ai Camera Hub.</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-14554\" id=\"attachment_14554\"><img alt=\"Camera Lupa Turret cảnh bảo còi và đèn hỗ trợ an ninh an toàn tuyệt đối\" decoding=\"async\" height=\"850\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4.jpg 850w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-300x300.jpg 300w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-100x100.jpg 100w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-600x600.jpg 600w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-150x150.jpg 150w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-768x768.jpg 768w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-4-50x50.jpg 50w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-14554\">Camera Lupa Turret cảnh bảo c&ograve;i v&agrave; đ&egrave;n hỗ trợ an ninh an to&agrave;n tuyệt đối</figcaption>\r\n</figure>\r\n\r\n<h3><b><i>1.5. T&iacute;ch hợp với AI Camera Hub, tạo n&ecirc;n giải ph&aacute;p an ninh to&agrave;n diện Ai Camera Lupa</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Ngo&agrave;i c&aacute;c t&iacute;nh năng được thiết kế ri&ecirc;ng, sản phẩm camera cảnh b&aacute;o Lupa Turret c&ograve;n c&oacute; thể t&iacute;ch hợp với bộ xử l&yacute; AI Camera Hub để hỗ trợ th&ecirc;m nhiều c&aacute;c t&iacute;nh năng ưu việt kh&aacute;c.</li>\r\n	<li aria-level=\"1\">Đặc bi&ecirc;t, AI Camera Hub c&ograve;n cung cấp th&ecirc;m một số t&iacute;nh năng ri&ecirc;ng chỉ &aacute;p dụng với Camera Lupa gi&uacute;p tăng cường trải nghiệm của người d&ugrave;ng.</li>\r\n</ul>\r\n\r\n<h2><b>2. Ưu điểm của giải ph&aacute;p an ninh Ai Camera Lupa</b></h2>\r\n\r\n<h3><b><i>2.1. Khoanh v&ugrave;ng an ninh, cảnh b&aacute;o tức thời nhờ tr&iacute; tuệ nh&acirc;n tạo AI</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bộ giải ph&aacute;p an ninh AI Camera Lupa được t&iacute;ch hợp tr&iacute; tuệ nh&acirc;n tạo gi&uacute;p ph&aacute;t hiện chuyển động của người ch&iacute;nh x&aacute;c tới 97%.</li>\r\n	<li aria-level=\"1\">Sau khi ph&aacute;t hiện người lạ, bộ giải ph&aacute;p an ninh sẽ ngay tức th&igrave; ph&aacute;t &acirc;m thanh, chớp đ&egrave;n xua đuổi kẻ trộm v&agrave; gửi tin nhắn cảnh b&aacute;o tới điện thoại người d&ugrave;ng.</li>\r\n	<li aria-level=\"1\">Từ đ&oacute; gia chủ ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m nhờ &ldquo;người bảo vệ&rdquo; th&ocirc;ng minh biết tự đuổi trộm v&agrave; ngăn chặn h&agrave;nh vi x&acirc;m nhập tr&aacute;i ph&eacute;p d&ugrave; ở bất kỳ đ&acirc;u.</li>\r\n</ul>\r\n\r\n<h3><b><i>2.2. H&igrave;nh ảnh m&agrave;u sắc n&eacute;t, sống động v&agrave;o ban đ&ecirc;m</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">C&aacute;c chuy&ecirc;n gia c&ocirc;ng nghệ của Lumi v&agrave; Pavana đ&atilde; nghi&ecirc;n cứu, t&iacute;ch hợp đ&egrave;n LED hồng ngoại v&agrave; LED &aacute;nh s&aacute;ng trắng cho camera cảnh b&aacute;o Lupa Turret.</li>\r\n	<li aria-level=\"1\">Nhờ đ&oacute; cải thiện &aacute;nh s&aacute;ng của khu vực theo d&otilde;i, tăng độ ch&iacute;nh x&aacute;c nhận diện người chuyển động, gi&uacute;p người d&ugrave;ng xem được h&igrave;nh ảnh c&oacute; m&agrave;u v&agrave;o ban đ&ecirc;m.</li>\r\n	<li aria-level=\"1\">Đặc biệt mắt thần ghi h&igrave;nh của bộ giải ph&aacute;p v ống k&iacute;nh 90.4&deg; @3.6mm rộng, c&ugrave;ng độ ph&acirc;n giải 2K gi&uacute;p cho h&igrave;nh ảnh ghi được sắc n&eacute;t, sống động v&agrave;o cả ban ng&agrave;y lẫn ban đ&ecirc;m</li>\r\n</ul>\r\n\r\n<h3><b><i>2.3. Quản l&yacute; chủ động, an to&agrave;n nhờ khả năng lưu trữ lớn tr&ecirc;n Cloud v&agrave; thẻ nhớ</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bộ giải ph&aacute;p an ninh AI Camera Lupa hỗ trợ lưu trữ theo 2 h&igrave;nh thức: Lưu trữ trong thẻ nhớ v&agrave; Lưu trữ đ&aacute;m m&acirc;y (Cloud).</li>\r\n	<li aria-level=\"1\">Với c&ocirc;ng nghệ P2P, b&ecirc;n cạnh app Lumi Life, giải ph&aacute;p an ninh Ai camera Lumi cho ph&eacute;p xem h&igrave;nh ảnh camera trực tuyến nội mạng v&agrave; ngoại mạng th&ocirc;ng qua nhiều nền tảng như tr&igrave;nh duyệt web, ứng dụng VLC.</li>\r\n	<li aria-level=\"1\">B&ecirc;n cạnh t&iacute;nh năng quản l&yacute; chủ động, bộ giải ph&aacute;p an ninh c&ograve;n gi&uacute;p gia chủ dễ d&agrave;ng c&agrave;i đặt th&ocirc;ng số luồng h&igrave;nh ảnh, cấu h&igrave;nh mạng v&agrave; cảnh b&aacute;o c&ograve;i đ&egrave;n ở khắp mọi nơi th&ocirc;ng qua ứng dụng LumiLife</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-14552\" id=\"attachment_14552\"><img alt=\"Camera Turet giúp quản lý chủ động, an toàn nhờ khả năng lưu trữ lớn trên Cloud và thẻ nhớ\" decoding=\"async\" height=\"849\" loading=\"lazy\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3.jpg 850w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-300x300.jpg 300w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-100x100.jpg 100w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-600x599.jpg 600w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-150x150.jpg 150w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-768x767.jpg 768w, https://lumi.vn/wp-content/uploads/2023/04/ung-dung-lumi-Camera-Turret-3-50x50.jpg 50w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-14552\">Camera cảnh b&aacute;o Lupa Turret gi&uacute;p quản l&yacute; chủ động, an to&agrave;n nhờ khả năng lưu trữ lớn tr&ecirc;n Cloud v&agrave; thẻ nhớ</figcaption>\r\n</figure>\r\n\r\n<h3><b><i>2.4. Đa dạng lựa chọn g&oacute;i giải ph&aacute;p an ninh nhờ bộ sản phẩm Camera trong nh&agrave;, ngo&agrave;i trời</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Camera Lupa đều phải trải qua b&agrave;i kiểm tra khắc nghiệt nhằm đảm bảo chất lượng bền bỉ v&agrave; khả năng chống chịu với m&ocirc;i trường khắc nghiệt để gi&uacute;p xua đuổi kẻ x&acirc;m nhập từ v&ograve;ng ngo&agrave;i ng&ocirc;i nh&agrave;.</li>\r\n	<li aria-level=\"1\">Camera Lupa ngo&agrave;i trời c&oacute; chỉ số chống nước ở mức độ cao IP66. Người d&ugrave;ng c&oacute; đa dạng lựa chọn g&oacute;i giải ph&aacute;p an ninh trong nh&agrave; hoặc ngo&agrave;i trời theo nhu cầu sử dụng.</li>\r\n</ul>\r\n\r\n<h3><b><i>2.5. T&iacute;ch hợp dễ d&agrave;ng v&agrave;o nh&agrave; th&ocirc;ng minh Lumi, gia tăng trải nghiệm sống tiện nghi</i></b></h3>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bộ giải ph&aacute;p Ai Camera Lupa l&agrave; một phần quan trọng gi&uacute;p ho&agrave;n thiện hệ thống Nh&agrave; th&ocirc;ng minh to&agrave;n diện của Lumi.</li>\r\n	<li aria-level=\"1\">Bộ Ai Camera Lupa dễ d&agrave;ng kết nối với c&aacute;c giải ph&aacute;p smarthome, smart lighting gi&uacute;p người d&ugrave;ng thiết lập ngữ cảnh dựa v&agrave;o nhu cầu sinh hoạt của gia đ&igrave;nh.</li>\r\n	<li aria-level=\"1\">Từ đ&oacute; n&acirc;ng cấp mức độ tự động h&oacute;a cho ng&ocirc;i nh&agrave; v&agrave; gia tăng trải nghiệm sống tiện nghi.</li>\r\n</ul>\r\n\r\n<h2><b>3. Ứng dụng của camera&nbsp; Lupa Turret</b></h2>\r\n\r\n<p>Với những ưu điểm nổi bật v&agrave; t&iacute;nh năng vượt trội, camera Turret c&oacute; thể ứng dụng cho rất nhiều khu vực kh&aacute;c nhau để bảo vệ v&agrave; gi&aacute;m s&aacute;t an ninh. Cụ thể:</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">C&aacute;c căn hộ chung cư</li>\r\n	<li aria-level=\"1\">Biệt thự, nh&agrave; vườn</li>\r\n	<li aria-level=\"1\">C&aacute;c văn ph&ograve;ng c&ocirc;ng sở</li>\r\n	<li aria-level=\"1\">C&aacute;c t&ograve;a nh&agrave; lớn</li>\r\n	<li aria-level=\"1\">Bệnh viện</li>\r\n	<li aria-level=\"1\">Trường học</li>\r\n	<li aria-level=\"1\">&hellip;</li>\r\n</ul>\r\n', 'upload/images/2.jpg', 1, 2, 0),
(46, 'Cổng tự động tay đòn STING', 23990000, 19000000, 99, 'Cổng tự động tay đòn STING là sản phẩm công nghệ cao, được thiết kế để đáp ứng nhu cầu kiểm soát ra vào tại nhà, biệt thự, toà nhà…. Với tính năng tự động hóa hoàn toàn, cổng thông minh tay đòn STING mang lại sự tiện ích và hiệu quả trong việc quản lý và bảo vệ khu vực.', '<h2><strong>1. Th&ocirc;ng số kỹ thuật</strong></h2>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Th&ocirc;ng số kỹ thuật</strong></td>\r\n			<td><strong>Bản 230V</strong></td>\r\n			<td><strong>Bản 24V</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>T&iacute;ch hợp bộ dừng cơ học</td>\r\n			<td>Kh&ocirc;ng</td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hỗ trợ lắp pin dự ph&ograve;ng</td>\r\n			<td>Kh&ocirc;ng</td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguồn v&agrave;o motor (V)</td>\r\n			<td>230V ~ &plusmn; 10 % (50/60 Hz)</td>\r\n			<td>24V ​</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Năng lượng ti&ecirc;u thụ tối đa (W)</td>\r\n			<td>300</td>\r\n			<td>50</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Momen xoắn cực đại (Nm)</td>\r\n			<td>1650</td>\r\n			<td>1000</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số lần đ&oacute;ng mở cửa tối đa trong 1 giờ</td>\r\n			<td>18</td>\r\n			<td>40</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số lần đ&oacute;ng mở cửa tối đa trong 24 giờ</td>\r\n			<td>90</td>\r\n			<td>90</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số lượng tụ điện</td>\r\n			<td>8uF</td>\r\n			<td>/</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nhiệt độ hoạt động l&yacute; tưởng (&deg;C)</td>\r\n			<td>-20 đến 50</td>\r\n			<td>-20 đến 50</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khả năng chịu nhiệt của motor (&deg;C)</td>\r\n			<td>150</td>\r\n			<td>/</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thời gian mở g&oacute;c 90&deg; (gi&acirc;y)</td>\r\n			<td>16</td>\r\n			<td>16</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trọng lượng sản phẩm khi đ&oacute;ng g&oacute;i (kg)</td>\r\n			<td>8.8</td>\r\n			<td>8.8</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Độ lớn &acirc;m thanh khi hoạt động (dBA)</td>\r\n			<td>&lt; 70</td>\r\n			<td>&lt; 70</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khả năng chống chịu nước v&agrave; bụi</td>\r\n			<td>IPX4</td>\r\n			<td>IPX4</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&gt;&gt; Xem th&ecirc;m sản phẩm:<a href=\"https://lumi.vn/san-pham/cong-truot-tu-dong-livi.html\" rel=\"noopener\" target=\"_blank\"><em><strong>&nbsp;Cổng trượt tự động LIVI</strong></em></a></p>\r\n\r\n<h2><strong>2. Ưu điểm cổng tự động tay đ&ograve;n STING</strong></h2>\r\n\r\n<ul>\r\n	<li>Cổng tự động STING c&oacute; thể sử dụng cho cổng xoay của biệt thự hoặc khu cơ quan. Động cơ n&agrave;y c&oacute; thể tải được cổng nặng tới 400kg với chiều d&agrave;i tối đa 3m, ph&ugrave; hợp với phần lớn nhu cầu sử dụng trong th&agrave;nh phố.</li>\r\n	<li>Sản phẩm được thiết kế nguy&ecirc;n khối, liền mạch, mang lại cảm gi&aacute;c chắc chắn v&agrave; sự gọn g&agrave;ng về kh&ocirc;ng gian. Đặc biệt, sản phẩm c&oacute; thể dễ d&agrave;ng t&iacute;ch hợp v&agrave;o hệ thống&nbsp;<a href=\"https://lumi.vn/\" rel=\"noopener\" target=\"_blank\">nh&agrave; th&ocirc;ng minh</a>&nbsp;của Lumi.</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-9755\" id=\"attachment_9755\"><img alt=\"Cổng tự động tay đòn thông minh STING 2-100 s\" decoding=\"async\" height=\"554\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s.jpg 1524w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s-600x391.jpg 600w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s-300x196.jpg 300w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s-1024x668.jpg 1024w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s-768x501.jpg 768w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-2-100-s-510x333.jpg 510w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-9755\">Cổng tự động tay đ&ograve;n th&ocirc;ng minh STING 2-100 s</figcaption>\r\n</figure>\r\n\r\n<h3><strong><em>2.1. Tự động ho&aacute; mở cổng</em></strong></h3>\r\n\r\n<ul>\r\n	<li>Với t&iacute;nh năng tự động hoạt động, cổng tự động tay đ&ograve;n STING tự động mở v&agrave; đ&oacute;ng th&ocirc;ng qua app điều khiển nh&agrave; th&ocirc;ng minh.</li>\r\n	<li>Sản phẩm gi&uacute;p tiết kiệm thời gian m&agrave; c&ograve;n đảm bảo sự thuận tiện v&agrave; an to&agrave;n cho người sử dụng.</li>\r\n</ul>\r\n\r\n<h3><em><strong>2.2. Thiết kế độc đ&aacute;o</strong></em></h3>\r\n\r\n<ul>\r\n	<li>Cổng tự động tay đ&ograve;n STING c&oacute; thiết kế độc đ&aacute;o với hệ thống c&aacute;nh cổng tay đ&ograve;n, tạo n&ecirc;n một diện mạo hiện đại v&agrave; thẩm mỹ cho c&ocirc;ng tr&igrave;nh.</li>\r\n	<li>K&iacute;ch thước nhỏ gọn với m&agrave;u sắc đen v&agrave; trắng; cho người d&ugrave;ng dễ d&agrave;ng lựa chọn ph&ugrave; hợp với kh&ocirc;ng gian c&ocirc;ng v&agrave; phong c&aacute;ch kiến tr&uacute;c.</li>\r\n</ul>\r\n\r\n<h3><strong><em>2.3. An to&agrave;n v&agrave; bảo mật</em></strong></h3>\r\n\r\n<ul>\r\n	<li>Cổng tự động tay đ&ograve;n STING được trang bị c&aacute;c t&iacute;nh năng an to&agrave;n v&agrave; bảo mật cao. Hệ thống cảm biến th&ocirc;ng minh gi&uacute;p ph&aacute;t hiện v&agrave; tr&aacute;nh va chạm, đảm bảo an to&agrave;n cho người v&agrave; phương tiện.</li>\r\n	<li>Ngo&agrave;i ra, t&iacute;nh năng kiểm so&aacute;t truy cập v&agrave; t&iacute;ch hợp với hệ thống gi&aacute;m s&aacute;t cũng đảm bảo sự an ninh v&agrave; bảo mật cho khu vực được bảo vệ.</li>\r\n</ul>\r\n\r\n<figure aria-describedby=\"caption-attachment-10112\" id=\"attachment_10112\"><img alt=\"Cổng tự động tay đòn thông minh STING ảnh thực tế\" decoding=\"async\" height=\"567\" loading=\"lazy\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te.jpg 1523w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te-600x400.jpg 600w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te-300x200.jpg 300w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te-1024x683.jpg 1024w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te-768x512.jpg 768w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-thong-minh-STING-anh-thuc-te-510x340.jpg 510w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-10112\">Cổng tự động tay đ&ograve;n th&ocirc;ng minh STING ảnh thực tế</figcaption>\r\n</figure>\r\n\r\n<p>Xem th&ecirc;m sản phẩm:&nbsp;<em><strong><a href=\"https://lumi.vn/san-pham/cong-tu-dong-tay-don-look/\" rel=\"noopener\" target=\"_blank\">Cổng tự động tay đ&ograve;n LOOK</a></strong></em></p>\r\n\r\n<h2><strong>3. T&iacute;nh năng nổi bật của c&ocirc;ng th&ocirc;ng minh tay đ&ograve;n STING</strong></h2>\r\n\r\n<p>Cổng tự động tay đ&ograve;n STING được trang bị một loạt c&aacute;c t&iacute;nh năng nổi bật, gi&uacute;p n&oacute; trở th&agrave;nh một giải ph&aacute;p đ&aacute;ng tin cậy trong việc kiểm so&aacute;t ra v&agrave;o. Dưới đ&acirc;y l&agrave; những t&iacute;nh năng quan trọng của cổng tự động tay đ&ograve;n STING:</p>\r\n\r\n<figure aria-describedby=\"caption-attachment-9611\" id=\"attachment_9611\"><img alt=\"Tính năng nổi bật của công thông minh tay đòn STING\" decoding=\"async\" height=\"473\" loading=\"lazy\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Anh-san-pham-STING-scaled.jpg\" width=\"851\" />\r\n<figcaption id=\"caption-attachment-9611\">T&iacute;nh năng nổi bật của c&ocirc;ng th&ocirc;ng minh tay đ&ograve;n STING</figcaption>\r\n</figure>\r\n\r\n<ul>\r\n	<li>Lắp pin dự ph&ograve;ng: Cổng tự động tay đ&ograve;n STING c&oacute; thể lắp pin dự ph&ograve;ng, gi&uacute;p duy tr&igrave; hoạt động của cổng khi mất điện. Đảm bảo rằng người d&ugrave;ng kh&ocirc;ng cần lo lắng về việc mất điện v&agrave; mất khả năng kiểm so&aacute;t ra v&agrave;o.</li>\r\n	<li>G&oacute;c mở cổng linh hoạt: Cổng th&ocirc;ng minh tay đ&ograve;n STING cho ph&eacute;p mở cổng đến g&oacute;c l&ecirc;n đến 110 độ; tạo ra sự linh hoạt trong việc kiểm so&aacute;t v&agrave; điều chỉnh lưu th&ocirc;ng, đ&aacute;p ứng nhu cầu của c&aacute;c khu vực c&oacute; d&ograve;ng xe qua lại lớn.</li>\r\n	<li>Thiết kế chắc chắn: Cổng tự động STING được thiết kế với động cơ chỉ 1 th&agrave;nh phần, tạo n&ecirc;n một hệ thống chắc chắn v&agrave; ổn định.Từ đ&oacute; mang đến hiệu suất hoạt động cao v&agrave; độ bền l&acirc;u d&agrave;i của cổng.</li>\r\n	<li>Tải trọng lớn: Cổng c&oacute; khả năng chịu tải trọng tối đa l&ecirc;n đến 400kg v&agrave; chiều d&agrave;i c&aacute;ch tối đa 3m; cho ph&eacute;p cổng hoạt động một c&aacute;ch ổn định v&agrave; linh hoạt trong việc điều khiển ra v&agrave;o cho c&aacute;c phương tiện c&oacute; k&iacute;ch thước v&agrave; trọng lượng lớn.</li>\r\n</ul>\r\n\r\n<p><em>Tải trọng tối đa của sản phẩm (biểu đồ m&agrave;u xanh)</em></p>\r\n\r\n<figure aria-describedby=\"caption-attachment-10114\" id=\"attachment_10114\"><img alt=\"Cổng-tự-động-tay-đòn-STING-tải-trọng\" decoding=\"async\" height=\"355\" loading=\"lazy\" sizes=\"(max-width: 500px) 100vw, 500px\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong.jpg 885w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong-600x426.jpg 600w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong-300x213.jpg 300w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong-768x545.jpg 768w, https://lumi.vn/wp-content/uploads/2021/05/Cong-tu-dong-tay-don-STING-tai-trong-510x362.jpg 510w\" width=\"500\" />\r\n<figcaption id=\"caption-attachment-10114\">Tải trọng tối đa của sản phẩm (biểu đồ m&agrave;u xanh)</figcaption>\r\n</figure>\r\n\r\n<h2><strong>4. Trong hộp sản phẩm STING c&oacute; g&igrave;?</strong></h2>\r\n\r\n<ul>\r\n	<li>02 cơ cấu vận h&agrave;nh, 230 VAC</li>\r\n	<li>01 Hộp điều khiển NET230N/C (đối với bản 230V) hoặc&nbsp;01 Hộp điều khiển NET24N/C (đối với bản 24V)</li>\r\n	<li>02 Điều khiển xa cầm tay 2 n&uacute;t</li>\r\n	<li>01 Bộ cảm biến an to&agrave;n</li>\r\n</ul>\r\n\r\n<h2><strong>5. Phụ kiện cho động cơ cổng tay đ&ograve;n STING</strong></h2>\r\n\r\n<h3><strong><em>5.1. Phụ kiện cho động cơ cổng tay đ&ograve;n STING &ndash; 24V</em></strong></h3>\r\n\r\n<figure aria-describedby=\"caption-attachment-10377\" id=\"attachment_10377\"><img alt=\"Phụ kiện cho động cơ cổng tay đòn STING - 24V\" decoding=\"async\" height=\"216\" loading=\"lazy\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-copy-100-1.jpg\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-10377\">Phụ kiện cho động cơ cổng tay đ&ograve;n STING &ndash; 24V</figcaption>\r\n</figure>\r\n\r\n<h3><strong><em>5.2. Phụ kiện cho động cơ cổng tay đ&ograve;n STING &ndash; 230V</em></strong></h3>\r\n\r\n<figure aria-describedby=\"caption-attachment-10372\" id=\"attachment_10372\"><img alt=\"Phụ kiện cho động cơ cổng tay đòn STING - 230V\" decoding=\"async\" height=\"495\" loading=\"lazy\" sizes=\"(max-width: 850px) 100vw, 850px\" src=\"https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-100-e1686804515613.jpg\" srcset=\"https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-100-e1686804515613.jpg 818w, https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-100-e1686804515613-600x349.jpg 600w, https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-100-e1686804515613-300x175.jpg 300w, https://lumi.vn/wp-content/uploads/2021/05/Tay-don-230V-phu-kien-cong-100-e1686804515613-768x447.jpg 768w\" width=\"850\" />\r\n<figcaption id=\"caption-attachment-10372\">Phụ kiện cho động cơ cổng tay đ&ograve;n STING &ndash; 230V</figcaption>\r\n</figure>\r\n', 'upload/images/10.jpg', 0, 0, 0),
